var vita = require('./index');
var interval;
var i = 1;
var toggle = false;

vita.open("usb:CLASS_AN2003/88888888", function(err, an2003) {
    if (err) {
        console.log(err);
    } else {
        an2003.startAgent(1, 0, 1000);
        an2003.on("data", function(data) {
            console.log(data);
        });
        an2003.on("error", function(err) {
            console.log("ERR>>>" + err);
        });
    }
});
