var assert = require('assert');
var common = require('./common');

var addon = require('../index');

describe('AN2003', function() {
	before(function() {
		this.dev = addon.open('usb:CLASS_AN2003'); 
	});
	after(function() {
		this.dev.close();
	});
	describe('#setProperty()', function() {
		common.channels(1, 4, 0 /*group*/, 1 /*property*/, 9 /*value*/, addon.prototype.setProperty);

		it('should return an error', function(done) {
			this.dev.setProperty(0, 0, 5, 9, common.invert(done));
		});
	});

	describe('#getInputs()', function() {
		common.channels(1, 16, 0, addon.prototype.getInputs);
	});
	
	describe('#setOutputs()', function() {
		common.channels(1, 16, 0, 0xDEAD, addon.prototype.setOutputs);
	});
});